var variable1 = localStorage.getItem("emailKey");
var variable2 = localStorage.getItem("passwordKey");

console.log(variable1, variable2);

window.onload = function() {
        let data = {
          user_email: variable1,
          user_password: variable2
        };
        //MAKE A REQUEST TO THE BACK END/API I SET UP IN PG-API DIRECTORY
        var request = new Request('http://localhost:3000/api/userDataPull', {
        method: 'POST',
        headers: new Headers({ 'Content-Type': 'application/json' }),
        body: JSON.stringify(data)
        });
        
        fetch(request)
          .then(function(response){
            response.json()
              .then(function(data) {
                console.log(data)
                if (data == 'failure') {
                  console.log('something is going wrong')
                } else if (data == 'success') {
                  console.log('hooray')
                  localStorage.setItem("emailKey", document.getElementById("user_login_email").value);
                  localStorage.setItem("passwordKey", document.getElementById("user_login_password").value);
                  window.location.replace('/Blogs');
                }
              })
          })
          .catch(function(err) {
            console.log(err)
          })
        
          document.getElementById("postMessage").innerHTML = 'Seems like this didnt work. Try again or create an account.';
  };


const Blogs = () => {
    return <div class="container">
        <br/>
        <br/>
        <br/>
        <br/>
        <h2>{variable1}</h2>
        </div>;
  };
  
  export default Blogs;
