var variable4 = localStorage.getItem("adminKey");

console.log(variable4);

const pullUsers=(e) => {
  e.preventDefault();
  let data = {
  };
  //MAKE A REQUEST TO THE BACK END/API I SET UP IN PG-API DIRECTORY
  var request = new Request('http://localhost:3000/api/pullUsers', {
  method: 'POST',
  headers: new Headers({ 'Content-Type': 'application/json' }),
  body: JSON.stringify(data)
  });
  
  fetch(request)
    .then(function(response){
      response.json()
        .then(function(data) {
          console.log(Object.values(data))
        })
    })
    .catch(function(err) {
      console.log(err)
    })
  }

const AdminPanel = () => {
  if (variable4 == 'User') {
    return <div class="container">
      <br/>
      <br/>
      <br/>
      <br/>
    <h2>You do not have access to this page.</h2>;
     </div>;
  } else if (variable4 == 'Admin') {
    return <div class="container">
      <br/>
      <br/>
      <br/>
      <br/>
    <h2>Welcome to the admin panel!</h2>;
    <br/>
    <br/>
    <p>Here you can view, delete, and change the role of all users in the Database!
      <br/><br/>
      Click the <strong>"Get Users"</strong> button to pull a list. Then, select a user by typing in the email. If you typed in the email correctly, two new buttons will appear to either 
      delete the user or change the role.</p>
      <br/>
      <br/>
      <button onClick={pullUsers} class="btn btn-dark">Admin Dashboard</button>
     </div>;
  } else if (variable4 == null) {
    return <div class="container">
      <br/>
      <br/>
      <br/>
      <br/>
    <h2>You need to login first.</h2>;
     </div>;
  }
  };
  
  export default AdminPanel;
