import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      title: 'Simple CRUD Application',
      countries: []
    }
  }

  // MAKE AJAX CALLS
componentDidMount() {
  console.log('COMPONENT HAS MOUNTED')
}

addCountry(event) {
event.preventDefault();
let data = {
  country_name: document.getElementById("country_name").ariaValueMax,
  continent_name: document.getElementById("continent_name").ariaValueMax
};
//MAKE A REQUEST TO THE BACK END/API I SET UP IN PG-API DIRECTORY
var request = new Request('http://localhost:3000/api/new-country', {
method: 'POST',
headers: new Headers({ 'Content-Type': 'application/json' }),
body: JSON.stringify(data)
});

fetch(request)
  .then(function(response){
    response.json()
      .then(function(data) {
        console.log(data)
      })
  })
}

render() {
  let title = this.state.title;
  return (
    <div className="App">
      <h1>{title}</h1>
      <form ref="countryForm">
        <input type="text" id="country_name" placeholder="country name"/>
        <input type="text" id="continent_name" placeholder="continent name"/>
        <button onClick={this.addCountry.bind(this)}>Add Country</button>
      </form>
    </div>
  );
}

}

export default App;
