var variable1 = localStorage.getItem("emailKey");
var variable2 = localStorage.getItem("passwordKey");
var variable3 = 'You need to login first.';

console.log(variable1, variable2);


window.onload = function() {
        let data = {
          user_email: variable1,
          user_password: variable2
        };
        //MAKE A REQUEST TO THE BACK END/API I SET UP IN PG-API DIRECTORY
        var request = new Request('http://localhost:3000/api/userDataPull', {
        method: 'POST',
        headers: new Headers({ 'Content-Type': 'application/json' }),
        body: JSON.stringify(data)
        });
        
        fetch(request)
          .then(function(response){
            response.json()
              .then(function(data) {
                console.log(data[0])
                document.getElementById('displayName').innerHTML = JSON.stringify(data[0]);
                document.getElementById('displayMessage').innerHTML = `Welcome! This is your page. Currently it shows a JSON version of your name and you can even change it! If you are an
                admin, then you will have access to see all other users in the database, and even delete them or change their roles.`
                document.getElementById('changeNameForm').style.display = 'initial';
                localStorage.clear();
              })
          })
          .catch(function(err) {
            console.log(err)
          })

  };


const Blogs = () => {
    return <div class="container">
        <br/>
        <br/>
        <br/>
        <br/>
        <h2 id="displayName">Must login first.</h2>
        <br/>
        <p id="displayMessage"></p>
        <br></br>
        <form id="changeNameForm">
        <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter Email"/>
        <input type="password" class="form-control" aria-describedby="emailHelp" placeholder="Enter Password"/>
        <input type="password" class="form-control" aria-describedby="emailHelp" placeholder="Enter New Name"/>
        <br/>
        <button type="submit" class="btn btn-dark">Change username</button>
        </form>
        </div>;
  };
  
  export default Blogs;
