import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      title: 'Simple CRUD Application',
      countries: []
    }
  }

  // MAKE AJAX CALLS
componentDidMount() {
  console.log('COMPONENT HAS MOUNTED')
}

addCountry(event) {
event.preventDefault();
let data = {
  user_name: document.getElementById("user_name").value,
  user_email: document.getElementById("user_email").value,
  user_password: document.getElementById("user_password").value
};
//MAKE A REQUEST TO THE BACK END/API I SET UP IN PG-API DIRECTORY
var request = new Request('http://localhost:3000/api/new-user', {
method: 'POST',
headers: new Headers({ 'Content-Type': 'application/json' }),
body: JSON.stringify(data)
});

fetch(request)
  .then(function(response){
    response.json()
      .then(function(data) {
        console.log(data)
      })
  })
  .catch(function(err) {
    console.log(err)
  })
}

render() {
  let title = this.state.title;
  return (
    <div className="App">
      <h1>{title}</h1>
      <form ref="signupForm">
        <input type="text" id="user_name" placeholder="User Name"/>
        <br/>
        <input type="text" id="user_email" placeholder="Email"/>
        <br/>
        <input type="password" id="user_password" placeholder="Password"/>
        <br/>
        <button onClick={this.addCountry.bind(this)}>Sign Up</button>
      </form>
    </div>
  );
}

}

export default App;
